import { useLotteryState, useLotteryBalance, useLotteryTimeStamp, useLotteryCounter } from "../hooks"

import { useEthers } from "@usedapp/core"
import helperConfig from "../helper-config.json"
import networkMapping from "../chain-info/deployments/map.json"
import { constants } from "ethers"
import brownieConfig from "../brownie-config.json"
import dapp from "../dapp.png"
import eth from "../eth.png"
import dai from "../dai.png"
import { makeStyles } from "@material-ui/core"



const useStyles = makeStyles((theme) => ({
    title: {
        color: theme.palette.common.white,
        textAlign: "center",
        padding: theme.spacing(4)
    }
}))

export const LotteryState = () => {
    const classes = useStyles()
    const lotteryState = useLotteryState()
    const counter = useLotteryCounter()
    return (
        <>
            <h2 className={classes.title}>State: {lotteryState ? lotteryState : -1}</h2>
            <h2 className={classes.title}>State: {counter ? lotteryState : -1}</h2>
        </>)
}
